step.context:
\textbf{Part (i)}
%text
The probability that both children
are boys given that atleast one is a boy 
is as follows. \textbf{Right or wrong?}
%

step.reason:
\begin{align}
	&P(N_B=2\vert\,N_B\geq 1) = \\
	&\underbrace{\dfrac{P(N_B\geq 1\vert\, N_B=2)\cdot P(N_B=2)}{P(N_B\geq 1)}}
	_{\text{Bayes' Theorem}}\\
	&\text{Now, }P(N_B\geq 1) = \underbrace{1-P(N_B=0)}_{N_B\neq 0\implies N_B\geq 1} \\
	&= 1-P(C_1\neq boy)\cdot P(C_2\neq boy)\\
	&=1-\frac{1}{2}\cdot\frac{1}{2} =  \frac{3}{4} \\
	&P(N_B = 2) = P(C_1=boy)\cdot P(C_2=boy) \\
	&= \frac{1}{2}\cdot\frac{1}{2}=\frac{1}{4}\\
	&\text{and }\underbrace{P(N_B\geq 1\vert\, N_B=2) = 1}_{\text{obviously}} \\
	&\therefore P(N_B=2\vert\,N_B\geq 1) = \dfrac{1\times\frac{1}{2}\cdot\frac{1}{2}}{\frac{3}{4}} = \frac{1}{3}
\end{align}

step.context:
\textbf{Part (ii) }
%text
Similarly, the probability that both 
children are boys given that the elder child is
a boy is as follows. \textbf{Right or wrong?}
%

step.reason:
\begin{align}
	&P(N_B = 2\vert\, C_1=boy) = \\
	&\underbrace{\dfrac{P(C_2=boy\vert\, N_B=2)\cdot P(N_B=2)}{P(C_2=boy)}}
	_{\text{Bayes' Theorem}}\\
			&\text{Now } P(C_1=boy\vert\, N_B=2) = 1\\
			&\text{(both boys}\implies C_1=boy)\\
			&P(C_1=boy) = \dfrac{1}{2} \\
			&P(N_B = 2) = \dfrac{1}{2}\cdot\dfrac{1}{2} = \dfrac{1}{4} \\
			&\therefore P(N_B = 2\vert\, C_1=boy) = \dfrac{1\times\frac{1}{4}}{\frac{1}{2}} = \dfrac{1}{2}
	\end{align}
