step.tex:
\begin{align}
  \text{Let }t = x^2+a^2 &\text{ and }(x-b) = A\dfrac{dt}{dx} + B \\
  \implies A &= \frac{1}{2}\text{ and } B = -b \\
  \implies (x-b)\cdot dx &= \frac{dt}{2} - b\cdot dx \\
  \implies I_E &= \int\sqrt{t}\cdot\underbrace{\left(\frac{dt}{2} - b dx\right)}
  _{ = (x-b)\cdot dx} \\
  &= \int\dfrac{\sqrt{t}}{2} dt - b\int\underbrace{\sqrt{x^2+a^2}}_{\sqrt{t}} dx
\end{align}

step.context:
\text{Given that }(x-3)\cdot dx =  \dfrac{dt}{2} -\dfrac{9}{2}\cdot dx \\
\text{will }I = \int (x-3)\cdot\sqrt{x^2+3x-18} \cdot dx\\
\text{evaluate to the following?}

step.tex:
\begin{align}
  I &= \dfrac{(x^2+3x-18)^\frac{3}{2}}{3} - \dfrac{9}{2}I_Z +C\text{ where } \\
  I_Z &= \frac{1}{2}\left[z\sqrt{z^2-a^2} - 
  \frac{a^2}{2}\log\vert z + \sqrt{z^2-a^2}\vert\right] \\
  z &= \left(x+\frac{3}{2}\right), a = \frac{9}{2} \\
\end{align}

step.reason:
\begin{align}
  I &= \int (x-3)\cdot\sqrt{\underbrace{x^2+3x-18}_{t}} \cdot dx \\
  &= \int\sqrt{t}\cdot 
  \underbrace{\left(\dfrac{dt}{2}-\dfrac{9}{2}\cdot dx\right)}_{ = (x-3)\cdot dx} \\
  &= \int \sqrt{t}\cdot \dfrac{dt}{2} - \dfrac{9}{2}\int\sqrt{x^2+3x-18}\cdot dx \\
  &= \dfrac{1}{2}\dfrac{t^{\frac{3}{2}}}{\frac{3}{2}}
  -\dfrac{9}{2}\int\sqrt{\left(x+\frac{3}{2}\right)^2 - \left(\frac{9}{2}\right)^2}\cdot dx \\
  &= \dfrac{(x^2+3x-18)^\frac{3}{2}}{3} - \dfrac{9}{2}
  \underbrace{\int\sqrt{z^2 - a^2}\cdot dz}_{I_Z} \\
  \text{where } z &= \left(x+\frac{3}{2}\right) \implies dz = dx, a = \frac{9}{2}\\
  I_Z &= \frac{1}{2}\left[z\sqrt{z^2-a^2} - 
  \frac{a^2}{2}\log\vert z + \sqrt{z^2-a^2}\vert\right]
\end{align}
