step.context:
\text{If }\left(\frac{2}{3}x-\frac{3}{2x}\right)^6 = 
\overbrace{\underbrace{T_1 + T_2 + \cdots + T_N }_{\text{N terms}}}^{\longrightarrow\text{ increasing powers of x}},\\
\text{then are the following true?}

step.reason:
\begin{align}
  \left(\frac{2x}{3}\right)^{m-1} &= A\cdot x^{m-1} \\
  \left(-\frac{3}{2x}\right)^{7-m} &= \frac{B}{x^{7-m}} \\
  \implies \left(\frac{2x}{3}\right)^{m-1}\cdot \left(-\frac{3}{2x}\right)^{7-m}
  &= A\cdot B\cdot \frac{x^{m-1}}{x^{7-m}} \\
  &= C\cdot x^{m-1-(7-m)} \\
  &= C\cdot x^{2m-8}
\end{align} \\
\text{Hence, for a term with }\frac{1}{x^2} \\
\begin{align}
  x^{2m-8} &= \frac{1}{x^2} = x^{-2} \\
  \implies 2m - 8 &= -2 \implies m = 3.
\end{align}

\text{With }m=3\text{ we get a term having }\frac{1}{x^2}.\\
\begin{align}
  T_3 &= ^6C_{3-1}\left(\frac{2x}{3}\right)^{3-1}
  \cdot \left(-\frac{3}{2x}\right)^{7-3} \\
  &= \underbrace{^6C_2\cdot \left(\frac{2}{3}\right)^2\cdot \left(-\frac{3}{2}\right)^{4}}_{K_3}
  \cdot\dfrac{x^2}{x^4} \\
  \implies K_3 &= 15\cdot\dfrac{4}{9}\cdot\dfrac{81}{16} = \dfrac{135}{4}
\end{align}
