\text{For a function to be continuous at}\\
\text{a point}\\
\begin{align}
    \lim_{x\to a^-}f(x) = 
    \lim_{x\to a^+}f(x) = f(a)
\end{align}

\text{For a function to be continuous at}\\
\text{a point} x = a\\
\begin{align}
    \lim_{x\to a^-}f(x) &= 
    \lim_{x\to a^+}f(x)
\end{align}
