step.context:
\text{The following substitution would convert} \\
I=\int \tan^2 \left(2x- 3\right)dx \text{ to } 
  \int \dfrac{\tan^2 t}{2}dt

step.tex:
\begin{align}
I &= \int \dfrac{\tan^2 t}{2}\, dt
    =\dfrac{1}{2}\left(\int sec^2  t\, dt - \int dt\right) \\
  &=\dfrac{\left(\tan t - t\right)}{2}+B
    =\dfrac{1}{2}\tan (2x-3) + x + C
\end{align}

\begin{align}
I &= \int \dfrac{\tan^2 t}{2}\, dt
    =\dfrac{1}{2}\left(\int sec^2  t\, dt - \int dt\right) \\
  &=\dfrac{\left(\sec t\,\tan t - t\right)}{2}+B \\
  &=\dfrac{1}{2}\sec (2x-3)\tan (2x-3) + x + C
\end{align}

step.reason:
\begin{align}
I &= \int \dfrac{\overbrace{\tan^2 t}^{\sec^2 t-1}}{2}\, dt
    =\dfrac{1}{2}\left(\overbrace{\int sec^2  t\, dt}^{\tan t} 
      - \int dt\right) \\
  &=\dfrac{\left(\tan t - t\right)}{2}+B
    =\dfrac{\tan(2x-3) - 2x + 3}{2}+B \\
  &=\dfrac{1}{2}\tan (2x-3) + x + C
\end{align}
