\begin{align}
  ax + by^2 &= \cos y \\
  \implies by^2-\cos y &= -ax \\
  \implies \underbrace{2by\cdot\dfrac{dy}{dx} + \sin y\dfrac{dy}{dx}}_{\textbf{Apply Chain Rule}}
   &= -a \\
  \implies \dfrac{dy}{dx} &= -\dfrac{a}{\sin y + 2by}
\end{align}

\begin{align}
  ax + by^2 &= \cos y \\
  \implies by^2-\cos y &= ax \\
  \implies \underbrace{2by\cdot\dfrac{dy}{dx} - \sin y\dfrac{dy}{dx}}_{\textbf{Apply Chain Rule}}
   &= a \\
  \implies \dfrac{dy}{dx} &= \dfrac{a}{2by - \sin y}
\end{align}
