step.context:
    \text{If }\underbrace{5x+3}_{\text{Numerator}} = 
		A\cdot\dfrac{d}{dx}(x^2+4x+10) + B \\
		\text{then what are A and B?}

step.reason:
\begin{align}
5x+3 &= A\cdot\frac{d}{dx}(x^2+4x+10) + B \\
\implies 5x+3 &= 2Ax + (4A + B) \\
\implies 2A=5\text{ or }A&=\frac{5}{2}\text{ and }\therefore B = -7
\end{align}

step.tex:
\begin{align}
  \text{Let }t &= x^2+4x+10 \implies dt = (2x+4)\cdot dx\\
  \therefore M &= \frac{5}{2}\int\dfrac{2x+4}{\sqrt{x^2+4x+10}}dx = \frac{5}{2}\int\dfrac{dt}{\sqrt{t}}\\
  &= \frac{5}{2}\cdot\dfrac{1}{\frac{1}{2}}\cdot\sqrt{t} + C_1 \\
  &= 5\sqrt{x^2+4x+10} + C_1
\end{align}
