step.context:
\small{\left(\vector{(2+3\lambda)}{(-1+4\lambda)}
  {(2+2\lambda)}\right)\cdot}
  \small{(\vector{1}{-}{1})=5} \\
\text{Are these the co-ordinates for the point of} \\
\text{interection?}

step.reason:
\small{\left(\vector{(2+3\lambda)}{(-1+4\lambda)}
  {(2+2\lambda)}\right)\cdot}
  \small{(\vector{1}{-}{1})}=\small{5} \\
\text{Taking scalar{\bf dot-product}} \\
\begin{align}
  &(2+3\lambda)-(-1+4\lambda)+(2+2\lambda)=5 \\
  \implies&\lambda+5=5\implies{\bf \lambda=0}
\end{align} \\
\text{Substitute $\lambda$ in the equation of the line} \\
\begin{align}
  \ora{r}&=\vector{2}{-}{2}+0(\vector{3}{4}{2}) \\
    &=\vector{2}{-}{2}
\end{align} \\
\text{Therefore co-ordinates are }{\bf(2, -1, 2).}
