step.reason:
\begin{align}
  &2\cos^2 x+3\sin x =0 \\
  \implies& 2 \left(1-\sin^2 x\right)+3\sin x = 0 \\
  \implies& 2-2\sin^2 x+3\sin x =0 \\
  \implies& 2\sin^2 x-3\sin x-2 =0
\end{align}

\begin{align}
  &2\sin^2 x-3\sin x-2=0 \\
  \implies &\sin x = \dfrac{3\pm\sqrt{3^2-4\cdot 2\cdot (-2)}}{2\cdot 2} \\
  \implies &\sin x = 2 \text{ or} -\dfrac{1}{2}  
\end{align} \\
\sin x \in [-1, 1], \text{ therefore the only valid value is } \\
{\bf\sin x=-\dfrac{1}{2}}
