\begin{align}
  R_1 &= \vert z_1\vert = \sqrt{a^2+b^2} \\
  R_2 &= \vert z_2\vert = \sqrt{c^2 + d^2} \\
  x-\iota y &= \sqrt{\dfrac{a-\iota b}{c-\iota d}} = \sqrt{\dfrac{z_1}{z_2}} \\
  &= \sqrt{\dfrac{R_1}{R_2}}\cdot e^{\left(\frac{\theta_1-\theta_2}{2}\right)\iota}
\end{align}

\begin{align}
  R_1 &= \vert z_1\vert = \sqrt{a^2+b^2} \\
  R_2 &= \vert z_2\vert = \sqrt{c^2 + d^2} \\
  x-\iota y &= \sqrt{\dfrac{a-\iota b}{c-\iota d}} = \sqrt{\dfrac{z_1}{z_2}} \\
  &= \sqrt{\dfrac{R_1}{R_2}}\cdot e^{\left(\theta_1-\theta_2\right)\iota}
\end{align}

reason.tex:
\text{Manipulating complex numbers is rather easy}\\
			\text{when one sees them as } z = R\cdot e^{\iota\theta}.\text{That said, }\\
			R_1=\vert z_1\vert\text{ and }R_2 = \vert z_2\vert\text{ are correct.}\\
			\text{As for }x-\iota y, \\
			\begin{align}
			  x-\iota y &= \sqrt{\dfrac{z_1}{z_2}} = 
			  \sqrt{\dfrac{R_1\cdot e^{\iota\theta_1}}{R_2\cdot e^{\iota\theta_2}}} \\
			  &= \underbrace{ 
			  \dfrac{\sqrt{R_1}\cdot e^{\frac{\iota\theta_1}{2}}}
			  {\sqrt{R_2}\cdot e^{\frac{\iota\theta_2}{2}}}}_{\sqrt{e^n} = (e^n)^\frac{1}{2} = e^{\frac{n}{2}}}
			  = \underbrace{\sqrt{\dfrac{R_1}{R_2}}\cdot e^{\left(\frac{\theta_1-\theta_2}{2}\right)\iota}}_
			  {\frac{e^m}{e^n} = e^{m-n}}
			\end{align}

\begin{align}
				\text{If }R\cdot e^{\iota\theta} &= z\text{ then } \\
				R\cdot e^{-\iota\theta} &= R\cdot[\cos(-\theta) + \iota\sin(-\theta)] \\
				&= \underbrace{R\cdot(\cos\theta - \iota\sin\theta) = \overline{z}}_
				{\cos(-\theta) = \cos\theta, \sin(-\theta) = -\sin\theta}
				\end{align}

\begin{align}
			(x+\iota y) &= \overline{x-\iota y} \\
			\text{And if, }x-\iota y &= R\cdot e^{\iota\theta}, \\
			\text{then }x+\iota y &= \underbrace{R\cdot e^{-\iota\theta}}_{\text{Step 2}}
			\end{align}\\
			\text{The rest is simply algebra.}
