options.tex:
\begin{align}
  &X\cdot\overline{X} = \dfrac{(x^2-1)+2\iota x}{2x^2+1}\cdot \dfrac{(x^2-1) - 2\iota x}{2x^2+1} \\
  &= \dfrac{\overbrace{
  [(x^2-1)+2\iota x]\cdot [(x^2-1)-2\iota x]}^{(a+b)\cdot(a-b) = a^2-b^2}
  }{(2x^2+1)^2} \\
  &= \dfrac{1}{(2x^2+1)^2}\cdot \left[(x^2-1)^2 - (2\iota x)^2\right] \\
  &= \dfrac{1}{(2x^2+1)^2}\cdot \left[(x^2-1)^2 + 4x \right] \\ 
  &= \dfrac{1}{(2x^2+1)^2}\cdot (x^2+1)^2 \\
  &\text{But }X\cdot \overline{X} = a^2 + b^2 \\ 
  &\therefore a^2+b^2 = \dfrac{(x^2+1)^2}{(2x^2+1)^2}
\end{align}

reason.tex:
\text{Just be careful with the algebra.} \\
					\text{And remember, }\\
					\begin{align}
					  (2\iota x)^2 &= -4x^2\implies -(2\iota x)^2 = 4x^2 \\
					  (x+a)^2 &= (x-a)^2 + 4ax 
					\end{align}
