statement.tex:
\text{Differentiate the following}\\
			\qquad f(x) = (\log x)^x + x^{\log x} \\
			\text{with respect to x.}

\\ \text{Given that }f(x) = \underbrace{(\log x)^x}_{g(x)} + 
  		\underbrace{x^{\log x}}_{h(x)} \\ 
  		\text{which of the following assertions is true?}

\\ \textbf{Right or wrong?} \\
				\text{Given that }g(x) = (\log x)^x,\, \dfrac{d}{dx}g(x)\text{ will}\\
				\text{evaluate to the following.}

\\ \textbf{Right or wrong?}\\
			\text{Similarly, given that }h(x) = x^{\log x},\text{ the}\\
			\text{following will be true.}

options.tex:
\begin{align}
  f(x) &= g(x) + h(x) \\ 
  \implies f'(x) &= g'(x) + h'(x)
\end{align}

\begin{align}
  f(x) &= g(x) + h(x) \\ 
  \implies\log f(x) &= \log g(x) + \log h(x) \\ 
  \implies \dfrac{1}{f(x)}\cdot f'(x) &= \dfrac{1}{g(x)}\cdot g'(x)
  + \dfrac{1}{h(x)}\cdot h'(x)
\end{align}

\begin{align}
  g(x) &= (\log x)^x \\
  \implies \log g(x) &= x\cdot \log (\log x) \\
  \therefore g'(x) &= (\log x)^{x-1}[ \,1 + (\log x) \cdot \log (\log x)]
\end{align}

reason.tex:
g'(x) = N\cdot x^{N-1}\text{ is true only when N is a known} \\ \text{constant. But taking }\log g(x)\text{ when }g(x) = h(x)^{j(x)}\\ \textbf{will always work.} \\ \text{And therefore, } \\ \begin{align} g(x) &= (\log x)^x \\ \implies \log g(x) &= x\cdot \log(\log x ) \end{align} \\
\begin{align}
\therefore \frac{1}{g(x)} g'(x) = \underbrace{\left[x\frac{d}{dx}\log (\log x) + \log (\log x ) \frac{d}{dx}x\right]}_{\textbf{Product Rule}} \\
= \left[ \underbrace{x\frac{d \log(\log x)}{d\log x }\cdot \frac {d \log x}{dx}}_{\textbf{Chain Rule}} + \log (\log x ) \frac{d}{dx}x\right] \\ \implies g'(x) = (\log x)^{x-1}[ \,1 + (\log x) \cdot \log (\log x)] \end{align}

options.tex:

  \begin{align}
			  h'(x) &= \log x\cdot x^{\log x - 1}\\
			  f'(x) &= g'(x) + h'(x) \\
			  &= (\log x)^{x-1}[ \,1+(\log x)\cdot \log (\log x)]\\
			  &+ \log x\cdot x^{\log x - 1}
			\end{align} \\
