statement.tex:
\\ %text
What are \underline\text{$A(x)$ and $B(x)$} in the following?
%
\begin{align}
Q &= \frac{1}{2}\int_0^{\frac\pi{2}}x\cdot\sec^2\frac{x}{2}\cdot dx  
=\left[ \underbrace{x\cdot A(x) + B(x) }_{\text{Integration by parts}}\right]_0^{\frac\pi{2}}
\end{align}

reason.tex:
\begin{align}
Q &= \frac{1}{2}\int_0^{\frac\pi{2}}x\cdot\sec^2\frac{x}{2}\cdot dx \\
&= \frac{1}{2}\left[ x\int_0^{\frac\pi{2}}\sec^2\frac{x}{2}
-\int_0^{\frac\pi{2}}\left(\frac{d}{dx}x\int\sec^2\frac{x}{2}\right)dx\right]\\
&=\frac{1}{2}\underbrace{\left[ x\cdot\frac{\tan\frac{x}{2}}{\frac{1}{2}}-\int\frac{\tan\frac{x}{2}}{\frac{1}{2}}\cdot dx \right]_0^{\frac\pi{2}}}
_{\int\sec^2 ax\cdot dx = \frac{\tan ax}{a}} \\
&= \left[ x\cdot\tan\frac{x}{2} - \int\tan\frac{x}{2}\cdot dx\right]_0^{\frac\pi{2}} \\
&= \underbrace{\left[ x\cdot\tan\frac{x}{2} + 2\log\left(\cos\frac{x}{2}\right)\right]_0^{\frac\pi{2}}}
_{\int\tan ax\cdot dx = -\frac{1}{a}\log (\cos ax), a = \frac{1}{2}}
\end{align}
%text

\underline\text{On comparing with \[ \left[x\cdot A(x) + B(x)\right]_0^{\frac\pi{2}}\]}, we can
see that 
%
\begin{align}
A(x) &= \tan\frac{x}{2} \\
\text{and }B(x) &= 2\log\left( \cos\frac{x}{2}\right)
\end{align}

\begin{align}
A &= \underbrace{\int_0^{\frac\pi{2}}\frac{\sin x}{1+\cos x}\cdot dx}_{P} + \underbrace{\int_0^{\frac\pi{2}}\frac{x}{1+\cos x}\cdot dx}_Q\\
\end{align} \\
\begin{align}
\text{where } P &= \log 2 \\
\text{and }Q &= \left[ x\cdot\tan\frac{x}{2} + 2\log\left( \cos\frac{x}{2}\right)\right]_0^{\frac\pi{2}}\\
&= \left[ \left(\frac\pi{2}\cdot\tan\frac\pi{4} + 2\log\cos\frac\pi{4}\right) - 0\right]\\
&= \frac\pi{2} + 2\log\frac{1}{\sqrt{2}}=\frac\pi{2} + 2\log (2^{-\frac{1}{2}})\\
&= \frac\pi{2} + 2\cdot\left(-\frac{1}{2}\right)\cdot\log 2 =\frac\pi{2}-\log 2\\
\therefore A &= P + Q = \log 2 + \left(\frac\pi{2} - \log 2\right) = \frac\pi{2}
\end{align}
