\underline\text{No. The friend is wrong. }

%
\begin{align}
\int_0^a f(x)\cdot dx &= \int_0^a f(a-x)\cdot dx \\
\text{and }\cos\left( \pi - x \right) &= -\cos x \\
\therefore \int_0^\pi \frac{e^{\cos x}\cdot dx}{e^{\cos x} + e^{-\cos x}}
&= \int_0^\pi \frac{e^{\cos (\pi-x)}\cdot dx}{e^{\cos (\pi-x)} + e^{-\cos (\pi-x)}}\\
&= \int_0^\pi\frac{e^{-\cos x}\cdot dx}{e^{-\cos x} + e^{\cos x}}
\end{align}

%text
\underline\text{Yes. The friend is correct.}

%
\begin{align}
\text{If }f(x) &= \frac{e^{\cos x}}{e^{\cos x} + e^{-\cos x}} \\
\text{and }g(x) &= \frac{e^{-\cos x}}{e^{-\cos x} + e^{\cos x}} \\
\text{then as } f(x) &\neq g(x) \\
\text{so }\int_0^\pi f(x)\cdot dx &\neq \int_0^\pi g(x)\cdot dx 
\end{align}

\begin{align} 
A &= \int_0^\pi\frac{e^{\cos x}\cdot dx}{e^{\cos x} + e^{-\cos x}} \\
&= \int_0^\pi\frac{e^{-\cos x}\cdot dx}{e^{-\cos x} + e^{\cos x}} \\
\therefore A + A &= \int_0^\pi dx = \pi \implies A = \frac\pi{2} 
\end{align}

reason.tex:
%text
We established in the last step that 

%
\begin{align} 
A &= \int_0^\pi\frac{e^{\cos x}\cdot dx}{e^{\cos x} + e^{-\cos x}} \\
&= \int_0^\pi\frac{e^{-\cos x}\cdot dx}{e^{-\cos x} + e^{\cos x}}
\end{align} \\ 

%text

Notice that the \underline\text{denominator is the same} in 
both expressions. 
%
\begin{align} 
\text{And }\therefore A + A &= \int_0^\pi\frac{e^{\cos x} + e^{-\cos x}\cdot dx}{e^{\cos x} + e^{-\cos x}}\\
&= \int_0^\pi dx = \pi \implies A = \frac\pi{2} 
\end{align}
