options.tex:
\begin{align}
P(B_3\vert A) &= \dfrac{P(A\vert B_3)\cdot P(B_3)}{\sum_{k=1}^3\quad P(A\vert B_k)\cdot P(B_k)}
\end{align}

reason.tex:
%text
This is simply \underline\text{Bayes' Theorem}.
%
\begin{align}
P(B_3\vert A)\cdot P(A) &= P(A\vert B_3)\cdot P(B_3) \\
\therefore P(B_3\vert A) &= \frac{P(A\vert B_3)\cdot P(B_3)}{P(A)}
\end{align} \\
%text

Moreover, in the above formula, 
%
\begin{align}
P(A) &= \underbrace{P(A\vert B_1)\cdot P(B_1) +\ldots + P(A\vert B_3)\cdot P(B_3)}_{\sum_{k=1}^3 \quad P(A\vert B_k)\cdot P(B_k)}
\end{align}

%text $P(A\vert B_1)$ is the probability of drawing a \underline\text{red and a white} ball \underline\text{from bag I}.
Similarly, $P(A\vert B_2)$ and $P(A\vert B_3)$ are the probabilities of drawing a red and a white ball from bags II and III respectively.
% \begin{tabular}{|c|c|c|} \hline \text{Bag} & \text{Total balls} & P(A\vert\text{ bag}) \\ \hline I & 6 & \frac{{^3C_1}\times{^1C_1}}{{^6C_2}} = \frac{1}{5} \\ \hline II & 4 & \frac{{^2C_1}\times{^1C_1}}{{^4C_2}} = \frac{1}{3} \\ \hline III & 9 & \frac{{^4C_1}\times{^2C_1}}{{^9C_2}} = \frac{2}{9} \\ \hline \end{tabular}

%text When a bag is picked initially, then each bag has an equal chance of being picked.
Which means $P(B_1) = P(B_2) = P(B_3) = \frac{1}{3} $
And therefore, % \begin{align} P(B_3\vert A) &= \underbrace{\frac{P(A\vert B_1)}{P(A\vert B_1) + P(A\vert B_2) + P(A\vert B_3)}}_ {P(B_1), P(B_2)\text{ and }P(B_3)\text{ all cancel out}} \\ &= \frac{\frac{2}{9}}{\frac{1}{5} + \frac{1}{3} + \frac{2}{9}} \\ &= \frac{5}{17} \end{align}
