options.tex:
\begin{align}
(x^2-y^2)\cdot dx &+2xy\cdot dy = 0 \\
\implies \frac{dy}{dx} &= 
\dfrac{\left(\frac{y}{x}\right)^2 - 1}{2\frac{y}{x}}
\end{align}
%text

The equation is of the form 
\[\quad  \frac{dy}{dx} = F\left( \frac{y}{x}\right) \implies \text{ homogenous equation} \]
%

reason.tex:
\begin{align}
\underbrace{v + x\cdot\frac{dv}{dx}}_{\frac{dy}{dx}} &= F\left(\frac{y}{x}\right) = \frac{v^2-1}{2v} \\
\implies x\cdot\frac{dv}{dx} &= \frac{v^2-1}{2v}- v = -\frac{1+v^2}{2v} \\
\therefore \frac{2v}{1+v^2}\cdot dv &= -\frac{dx}{x} \\
\implies \int\frac{2v}{1+v^2} \cdot dv &= -\int \frac{dx}{x} 
\end{align}

\begin{align}
z = 1+v^2&\implies dz = 2v\cdot dv \\
\therefore \int\frac{2v}{1+v^2}\cdot dv &= \int\frac{dz}{z} \\ 
&= \log\vert z\vert + A \\
&= \log\vert 1+v^2\vert +A
\end{align}

options.tex:
\begin{align}
\int\frac{2v}{1+v^2}\cdot dv &= -\int\frac{dx}{x} \\
\implies (1+v^2)\cdot x &= \left( 1+\frac{y^2}{x^2} \right)\cdot x = C \\
\text{As }y =1\text{ when } &x = 1, \text{ so } C = 2 \\
\therefore x^2 + y^2 &= 2x \text{ (final answer) }
\end{align}

reason.tex:
\begin{align} \int\frac{2v}{1+v^2}\cdot dv &= -\int\frac{dx}{x} \\ \implies \log \vert 1+v^2\vert &= -\log \vert x \vert + B \\ \text{or }\log \vert 1+v^2\vert + \log\vert x\vert &= B \\ \implies \underbrace{\log\vert (1+v^2)\cdot x\vert}_{\log a + \log b = \log (a\cdot b)} &= B \\ \implies (1+v^2)\cdot x & = e^B = C \end{align}
%text
\underline\text{But do not forget that $y=1$ when $x=1$}. And therefore % \begin{align} (1+v^2)\cdot x &= C \implies \left( 1 + \frac{y^2}{x^2}\right)\cdot x = C\\ \text{And if }y = 1&\text{ when }x = 1\text{ then } \\ \left( 1 + \frac{1^2}{1^2}\right)\cdot 1 &= C \implies C = 2 \\ \therefore \left( 1 + \frac{y^2}{x^2}\right)\cdot x &= 2 \implies x^2 + y^2 = 2x \end{align}
