%text
\textbf{Rationalize and simplify }

%
\begin{align}
\text{Let }P(x) &= \left[ \dfrac{\sqrt{1+\sin x} + \sqrt{1-\sin x}}{\sqrt{1+\sin x} - \sqrt{1-\sin x}}\right] \\ 
&= \left[ \dfrac{\sqrt{X} + \sqrt{Y}}{\sqrt{X} - \sqrt{Y}} \right] \\
&= \left[ \dfrac{\sqrt{X} + \sqrt{Y}}{\sqrt{X} - \sqrt{Y}} \right]\cdot
\dfrac{\sqrt{X}-\sqrt{Y}}{\sqrt{X}-\sqrt{Y}} \\
&= \dfrac{X-Y}{(\sqrt{X}-\sqrt{Y})^2}
\end{align}

\textbf{Start with Chain Rule} \\
\begin{align}
 y &= \cot^{-1}\left[ \dfrac{\sqrt{1+\sin x} + \sqrt{1-\sin x}}{\sqrt{1+\sin x} - \sqrt{1-\sin x}}\right] \\
 \text{or }y &= \cot^{-1} P(x) \\
 \therefore \frac{dy}{dx} &= \underbrace{\frac{d}{d P(X)}\cot^{-1} P(x)\cdot \frac{d}{dx} P(x) }_{\text{Chain Rule}}
\end{align}

reason.tex:
\begin{align}
P(x) &= \left[ \dfrac{\sqrt{1+\sin x} + \sqrt{1-\sin x}}{\sqrt{1+\sin x} - \sqrt{1-\sin x}}\right] \\
&= \underbrace{\left[ \dfrac{\sqrt{X} + \sqrt{Y}}{\sqrt{X} - \sqrt{Y}} \right]\cdot
\dfrac{\sqrt{X}-\sqrt{Y}}{\sqrt{X}-\sqrt{Y}}}_{\text{See Step 1}} \\
&= \dfrac{X-Y}{(\sqrt{X} - \sqrt{Y})^2} \\
&= \dfrac{(1+\sin x) - (1-\sin x)}{\left[ \sqrt{1+\sin x} - \sqrt{1-\sin x}\right]^2} \\
&= \dfrac{2\sin x}{\underbrace{(1+\sin x) + (1-\sin x) -2\sqrt{1-\sin^2 x}}_{(a-b)^2 = a^2+b^2-2ab}}\\
&= \dfrac{2\sin x}{2-2\cos x} = \dfrac{\sin x}{1-\cos x}
\end{align}
