%text Let the given events be defined as follows
% \begin{tabular}{|c|c|c|c|} \hline & \text{Meaning} & & \text{Meaning} \\ \hline S & \text{Rides a scooter} & C & \text{Drives a car} \\ \hline T & \text{Drives a truck} & A & \text{Has an accident} \\ \hline \end{tabular}
%text
We have therefore been told that
% \begin{tabular}{|c|c|} \hline P(S) = \frac{1}{6}&P\left( S\,\vert\, A \right) = 0.01 \\ \hline P(C) = \frac{1}{3}& P\left( C\,\vert\,A \right) = 0.03 \\ \hline P(T) = \frac{1}{2}&P\left( T\,\vert\, A\right) = 0.15 \\ \hline
\end{tabular}

reason.tex:
%text
As per Bayes' Theorem
\[\qquad  P\left( S\,\vert\, A\right) = \dfrac{P\left( A\,\vert\, S\right)\cdot P(S)}{P(A)}\]
%

\begin{align}
\text{Now, }P(A) &= P(A \vert S)\cdot P(S) + \ldots + P(A\vert T)\cdot P(T) \\
&= \underbrace{0.01\times\frac{1}{6} + 0.03\times\frac{1}{3} + 0.15\times\frac{1}{2}}_{\text{Theorem of Total Probability}}\\
\therefore P\left( S\,\vert\, A\right) &= \dfrac{0.01\times \frac{1}{3}}
{0.01\times\frac{1}{6} + 0.03\times\frac{1}{3} + 0.15\times\frac{1}{2}} \\
&= \frac{1}{52}
\end{align}
