statement.tex:
\\ \textbf{Right or wrong?}\\
				\text{Given }T_m\text{ from Step 1, the coefficient of }\\
				x^6y^3\text{ will be the following.}

reason.tex:
\begin{align}
			  (x+y)^9 &= \sum_{m=0}^9\,^9C_m\cdot x^m\cdot y^{9-m}, 0\leq m\leq 9\\
			  \implies T_m &= ^9C_m\cdot x^m\cdot y^{9-m}
			\end{align}
