step.context:
\textbf{Right or wrong?}\text{ - the rate of change of}\\
\text{the perimeter (P) will be given by the }\\
\text{following calculations?}

step.tex:
\begin{align}
P&=\text{ perimeter } = 2\cdot( x + y) \\
\implies\dfrac{dP}{dt} &= 2\cdot\left[ \dfrac{dx}{dt}
+\dfrac{dy}{dt}\right]\\
&= 2\cdot\left( -5\text{ cm/min}+4\text{ cm/min}\right)\\
&= -2\text{ cm/min}
\end{align}

\begin{align}
P&=\text{ perimeter } = 4\cdot( x + y) \\
\implies\dfrac{dP}{dt} &= 4\cdot\left[ \dfrac{dx}{dt}
+\dfrac{dy}{dt}\right]\\
&= 4\cdot\left( -5\text{ cm/min}+4\text{ cm/min}\right)\\
&= -4\text{ cm/min}
\end{align}

\begin{align}
A &= x\cdot y \\
\implies\dfrac{dA}{dt} &= x\cdot\frac{dy}{dt} + y\cdot\frac{dx}{dt} \\
\implies\left[ \frac{dA}{dt}\right]_{x=8,y=6} &= 
8\text{ cm}\cdot 4\text{ cm/min} \\
&+ 6\text{ cm}\cdot -5\text{cm/min}\\
&= 2\text{ cm$^2$/min}
\end{align}

\begin{align}
A &= x\cdot y \\
\implies\dfrac{dA}{dt} &=\frac{dx}{dt} \times \frac{dy}{dt} \\
\implies\left[ \frac{dA}{dt}\right]_{x=8,y=6} &= 
-5\text{ cm/min}\times 4\text{ cm/min}\\
&= -20\text{ cm$^2$/min$^2$}
\end{align}
