statement.tex:
\text{Differentiate the following function}\\
\text{with respect to x}\\
\quad f(x) = \dfrac{\sin (ax +b)}{\cos (cx + d)}

step.tex:
	\begin{tabular}{|c|c|}
	\hline 
	\text{Function} & \text{Derivative} \\
	\hline
	g(x) = \sin (ax + b) & g'(x) = a\cdot\cos (ax + b) \\
	\hline 
	h(x) = \cos (cx + d) & h'(x) = -c\cdot\sin (cx + d) \\
	\hline
	\end{tabular} \\
	\begin{align}
	  &\therefore \dfrac{d}{dx}f(x) = \dfrac{h(x)\cdot\frac{d}{dx}g(x) - g(x)\cdot\frac{d}{dx}h(x)}{h(x)^2}\\
	  &= \dfrac{a\cos(ax+b) + c\sin (ax+b)\cdot\tan(cx + d)}{\cos (cx + d)}
  \end{align}
