statement.tex:
\text{Differentiate the following} \\ 
			\qquad f(x) = e^x + e^{x^2} + \ldots + e^{x^5} \\
			\text{with respect to x.}

reason.tex:
\textbf{Don't be scared of }\sum.\text{ It is only a compact}\\
  		\text{way of writing the sum of many terms.} \\
  		\text{That said, }\\
  		\underbrace{\dfrac{d}{dx}f(x) = \dfrac{d}{dx}\sum_{k=1}^n a_k(x)}_{\text{derivative of sums}}
  		= \underbrace{\sum_{k=1}^n\dfrac{d}{dx}a_k(x)}_{\text{sum of derivatives}}

options.tex:
\begin{align}
\dfrac{d}{dx}f(x) &= \sum_{k=1}^5 k\cdot x^{k-1}\cdot e^{x^k} \\
&= e^x + 2x\cdot e^{x^2} +\ldots + 5x^4\cdot e^{x^5} 
\end{align}

reason.tex:
\textbf{The k-th term }\text{in f(x) looks like }\\
  		\begin{align}
  		a_k(x) &= e^{x^k} \\
  		\implies \frac{d}{dx}a_k(x) &= \underbrace{\frac{d}{d x^k}e^{x^k}\cdot \frac{d}{dx}x^k}
  		_{\text{Chain Rule}} \\
  		&= k x^{k-1}\cdot e^{x^k} \\
  		\therefore \frac{d}{dx}f(x) &= \sum_{k=1}^5 \frac{d}{dx}a_k(x) \\
  		&= \sum_{k=1}^5 k\cdot x^{k-1}\cdot e^{x^k} \\
  		&= e^x + 2x\cdot e^{x^2} +\ldots + 5x^4\cdot e^{x^5}
  		\end{align}
