statement.tex:
\text{Differentiate the following} \\
\qquad f(x) = \dfrac{\cos x}{\log x}\\
\text{with respect to x.}

\\ \textbf{Right or wrong? } \\
	  		\text{If }f(x) = \dfrac{g(x)}{h(x)},\text{ then }\frac{d}{dx}f(x)\text{ is}\\
	  		\text{given by the following.}

options.tex:
\begin{align}
  \dfrac{d}{dx}f(x) &= \dfrac{\log x\cdot\frac{d}{dx}\cos x - \cos x\cdot\frac{d}{dx}\log x}{(\log x)^2} \\
  &= -\dfrac{\log x\cdot\sin x + \frac{\cos x}{x}}{(\log x)^2} \\
  &= -\dfrac{x\cdot\sin x\cdot\log x + \cos x}{x\cdot(\log x)^2}, x\gt 0, x\neq 1.
\end{align}

reason.tex:
\text{Note the use of the two standard results}\\
        \frac{d}{d\theta}\cos\theta = -\sin\theta \text{ and }
				\frac{d}{dy}\log y = \frac{1}{y}
