statement.tex:
\text{Differentiate the following }\\
		\qquad f(x) = x^{x \cos x} + \, \dfrac{x^2 + 1}{x^2 -1}\\
		\text{with respect to x.}

reason.tex:
\text{Finding }\log f(x) \text{ would have helped had }\\
                        f(x) = a\cdot b\text{ or }\frac{a}{b}.\text{But it is not. }f(x) = a + b. \\
                        \text{ Taking its log wouldn't help.}

options.tex:
\begin{align}
g(x) &=  x^{x \cos x}\\
 \implies g'(x) 
&= \underbrace{(x \cos x) \cdot x^{x \cos x- \,1}}_{\frac{d}{dx}x^n = nx^{n-1}}
\end{align}

reason.tex:
g'(x) = N\cdot x^{N-1}\text{ is true only when N is a known} \\
\text{constant. But taking }\log g(x)\text{ when }g(x) = h(x)^{j(x)}\\
\textbf{ will always work.}

options.tex:
\begin{align}
f(x) &= \underbrace{x^{x \cos x}}_{g(x)} + \underbrace{\left[ \dfrac{x^2 + 1}{x^2 -1}\right] }_{h(x)} \\
\implies f'(x) &= g'(x) + h'(x) \\
\text{Now, }h(x) &= \quad \, \, \left[ \dfrac{x^2 + 1}{x^2 -1}\right]
\\ \implies\log h(x) &= \log \left[ \dfrac{x^2 + 1}{x^2 -1}\right] \\
 \frac{1}{h(x)}h'(x) &= \underbrace{\left[ \dfrac{(x^2 -1)(2x) - (x^2 +1)(2x)}{(x^2 -1)^2} \right]}_{\textbf{Quotient Rule}} \\

\implies h'(x) &= \dfrac{-4 x}{(x^2 -1)^2}   \\
\end{align}\\

\begin{align}

\therefore f'(x) &= x^{x \cos x} \left[\, \cos x \cdot (1 + \log x) - x \sin x \cdot \log x \right] \\
& \quad -  \dfrac{4 x}{(x^2 -1)^2} 
\end{align}

reason.tex:
\text{We found }g'(x) \text{in the last step. We only need}\\
\text{to find }h'(x)\text{, where }h(x) = \dfrac{x^2 + 1}{x^2 -1}\text{ by taking }\\
\log h(x)\text{ first.}

choices.tex:
\begin{align}
f'(x) = &x^{x \cos x} \left[\, \cos x \cdot (1 + \log x) - x \sin x \cdot \log x \right] \\
& -  \dfrac{4 x}{(x^2 -1)^2} 
\end{align}

\begin{align}
f'(x) = &x^{x \cos x} \left[\, \cos x \cdot (1 + \log x) - x \sin x \cdot \log x \right] \\
& -  \dfrac{4 x}{(x^2 -1)} 
\end{align}

\begin{align}
f'(x) = &x^{x \cos x} \left[\, \cos x \cdot (1 + \log x) - x \sin x \cdot \log x \right] \\
& +  \dfrac{4 x}{(x^2 -1)^2} 
\end{align}

\begin{align}
f'(x) = &x^{x \cos x} \left[\, \cos x \cdot (1 + \log x) + x \sin x \cdot \log x \right] \\
& -  \dfrac{4 x}{(x^2 -1)^2} 
\end{align}
