statement.tex:
\text{Differentiate the following}\\
		\qquad f(x) = x^{\sin x} + (\sin x)^ {\cos x} \\
		\text{with respect to x.}

options.tex:
\begin{align}
f(x) & = x^{\sin x} + (\sin x)^{\cos x}\\
\implies f'(x) &= \frac{d}{dx}x^{\sin x} + \frac{d}{dx}(\sin x)^{\cos x}
\end{align}

reason.tex:
\text{Finding }\log f(x) \text{ would have helped had }\\
                        f(x) = a\cdot b\text{ or }\frac{a}{b}.\text{But it is not. }f(x) = a + b. \\
                        \text{ Taking its log wouldn't help.}

options.tex:
\begin{align}
g(x) &=  x^{\sin x}\\
 \implies \log g(x) &= \sin x \cdot \log x \\ 
\end{align}\\

\begin{align}
\therefore \frac{1}{g(x)} g'(x) &= \underbrace{\left[\sin x\frac{d}{dx}\log x
+ \log x\frac{d}{dx}\sin x\right ]}_{\textbf{Product Rule}} \\

\implies g'(x) &= x^{\sin x} \left[\, \frac {\sin x}{x}+ (\cos x) \cdot \log x \right]
\end{align}

\begin{align}
g(x) &= x^{\sin x}\\
 \implies g'(x) 
&= \underbrace{(\sin x) \cdot x^{\sin x- \,1}}_{\frac{d}{dx}x^n = nx^{n-1}}
\end{align}

reason.tex:
g'(x) = N\cdot x^{N-1}\text{ is true only when N is a known} \\
\text{constant. But taking }\log g(x)\text{ when }g(x) = h(x)^{j(x)}\\
\textbf{ will always work.}

options.tex:
\begin{align}
f(x) &= \underbrace{x^{\sin x}}_{g(x)} + \underbrace{(\sin x) ^{\cos x}}_{h(x)} \\
\implies f'(x) &= g'(x) + h'(x) \\
\text{Now, }h(x) &= (\sin x)^{\cos x}
\\ \implies\log h(x) &= \cos x\cdot\log \sin x \\
\implies \frac{1}{h(x)}h'(x) &= \cot x \cdot \cos x -\sin x \cdot \log (\sin x) \\
\end{align}\\
\begin{align}
\text{So, } h'(x) &=   (\sin x)^{\cos x} \left[ \cot x \cdot \cos x -\sin x \cdot \log (\sin x) \right] \\
\therefore f'(x) &= x^{\sin x} \left[\, \frac {\sin x}{x}+ (\cos x) \cdot \log x \right] +\\
\quad (&\sin x)^{\cos x} \left[ \cot x \cdot \cos x -\sin x \cdot \log (\sin x) \right]
\end{align}

reason.tex:
\text{We found }g'(x) \text{in the last step. We only need}\\
\text{to find }h'(x)\text{, where }h(x) = (\sin x)^{\cos x}\text{ by taking }\\
\log h(x)\text{ first.}
