statement.tex:
\text{Differentiate the following}\\
\qquad f(x) = x^x - 2^{\sin x} \\
\text{with respect to x.}

step.tex:
\begin{align}
  f(x) &= x^x - 2^{\sin x} \\
  \implies \log f(x) &= \log x^x - \log 2^{\sin x} \\
  &= x\log x - \sin x\cdot \log 2 \\
  \therefore f'(x) &= \frac{d}{dx}(x\log x) - \log 2\frac{d}{dx}\sin x
\end{align}

step.reason:
\text{Finding }\log f(x) \text{ would have helped had }\\
f(x) = a\cdot b\text{ or }\frac{a}{b}.\text{But it is not. }f(x) = a - b. \\  
\text{ Taking its log wouldn't help.}

step.tex:
\begin{align}
  g(x) &= x^x \implies \log g(x) = x\cdot \log x \\ 
  \therefore \frac{1}{g(x)} g'(x) &= \underbrace{\left[x\frac{d}{dx}\log x 
  + \log x\frac{d}{dx}x\right]}_{\textbf{Chain Rule}} \\
  \implies g'(x) &= x^x\cdot (1+\log x)
\end{align}

step.reason:
g'(x) = N\cdot x^{N-1}\text{ is true only when N is a known} \\
\text{constant. But taking }\log g(x)\text{ when }g(x) = h(x)^{j(x)}\\
\textbf{ will always work.}

step.tex:
\begin{align}
f(x) &= \underbrace{x^x}_{g(x)} - \underbrace{2^{\sin x}}_{h(x)} \\
  \implies f'(x) &= g'(x) - h'(x) \\
  \text{Now, }h(x) = 2^{\sin x} &\implies\log h(x) = \sin x\cdot\log 2 \\
  \implies \frac{1}{h(x)}h'(x) &= \log 2\cdot\cos x \\
  \implies h'(x) &= \log 2\cdot 2^{\sin x}\cdot cos x \\
  \therefore f'(x) = x^x\cdot (1+\log x) &-\log 2\cdot\cos x\cdot 2^{\sin x}
\end{align}
