statement.tex:
\text{Differentiate the following}\\
 f(x) = (\sin x)^{x} + \sin^{-1} {\sqrt x} \\
\text{with respect to x.}

options.tex:
\begin{align}
f(x) & = (\sin x)^{x} + \sin^{-1} {\sqrt x} \\
\implies f'(x)&= \frac{d}{dx}(\sin x)^{x} + \frac{d}{dx}(\sin^{-1} {\sqrt x})
\end{align}

reason.tex:
\text{Finding }\log f(x) \text{ would have helped had }\\
                        f(x) = a\cdot b\text{ or }\frac{a}{b}.\text{But it is not. }f(x) = a + b. \\
                        \text{ Taking its log wouldn't help.}

g'(x) = N\cdot x^{N-1}\text{ is true only when N is a known} \\
\text{constant. But taking }\log g(x)\text{ when }g(x) = h(x)^{j(x)}\\
\textbf{ will always work.}

options.tex:
\begin{align}
f(x) &= \underbrace{(\sin x)^{x}}_{g(x)} + \underbrace{\sin^{-1} {\sqrt x}}_{h(x)} \\
\implies f'(x) &= g'(x) + h'(x) \\
\text{Now, }h(x) &= \sin^{-1} {\sqrt x}
\\ \implies\log h(x) &= \log (\sin^{-1} {\sqrt x}) \\
\implies \frac{1}{h(x)}h'(x) &= \dfrac{1}{\sin^{-1} {\sqrt x}} \cdot \dfrac{1}{\sqrt{1-x}} \cdot \dfrac{1}{2 \sqrt{x}} \\
\implies h'(x) &=   \dfrac{1}{2\, \sqrt{x - x^2}} \\
\end{align}\\

\therefore f'(x) = (\sin x)^{x} \left[ x \cdot \cot x + \log (\sin x)\right] + \dfrac{1}{2 \sqrt{x - x^2}}

reason.tex:
\text{We found }g'(x) \text{in the last step. We only need}\\
\text{to find }h'(x)\text{, where }h(x) = \sin^{-1} {\sqrt x} \text{ by taking }\\
\log h(x)\text{ first.}
