statement.tex:
\\ \text{If }\underbrace{x+2}_{\text{Numerator}} = 
  		A\cdot\dfrac{d}{dx}(x^2+2x+3) + B \\
  		\text{then what are A and B?}

reason.tex:
\begin{align}
      x+2 &= A\cdot\frac{d}{dx}(x^2+2x+3) + B \\
      \implies x+2 &= 2Ax + (2A + B) \\
      \implies 2A=1\text{ or }A&=\frac{1}{2}\text{ and }\therefore B = 1
      \end{align}

options.tex:
\begin{align}
  \text{Let }t &= x^2+2x+3 \implies dt = (2x+2)\cdot dx\\
  \therefore M &= \frac{1}{2}\int\dfrac{2x+2}{x^2+2x+3}dx = \frac{1}{2}\int\dfrac{dt}{\sqrt{t}}\\
  &= \frac{1}{2}\cdot\dfrac{1}{\frac{1}{2}}\cdot\sqrt{t} + C_1 \\
  &= \sqrt{x^2+2x+3} + C_1
\end{align}
