statement.tex:
\text{Integrate the following}\\
\qquad x\cdot\sec^2 x \\
\text{using integration by parts.}

reason.tex:
\begin{align}
  		  I &= \int x\cdot\sec^2 x dx \\ 
  		    &= x\cdot\int\sec^2x dx - \int\left[\frac{d}{dx}x\cdot\int\sec^2x\cdot dx\right]\cdot dx \\
  		    &= x\cdot\tan x - \int\tan x\cdot dx 
  		\end{align}

\begin{align}
  			  A &= \int\tan x\cdot dx = \int\dfrac{\sin x}{\cos x}\cdot dx \\ 
  			  \text{Let }\cos x &= y \implies -\sin x\cdot dx = dy \\
  			  \therefore A &= \int\dfrac{\sin x}{\cos x}\cdot dx = -\int\dfrac{dy}{y} \\
  			  &= -\log\vert y\vert = -\log \vert\cos x\vert 
  			\end{align}

\begin{align}
  				  I &= x\cdot\tan x - \underbrace{\int\tan x\cdot dx}_A \\
  				  &= x\cdot\tan x -\left(-\log\vert \cos x\vert \right) + C \\
  				  &= x\cdot\tan x + \log\vert\cos x\vert + C 
  				\end{align}
