\begin{align}
  \ora{a}=\vector{1}{1}{1},\, \ora{c}&=\vector{x}{y}{z},\, \ora{b}=\vector{}{1}{-1} \\
  \ora{a}\times\ora{c}=\left\vert \begin{array}{ccc}
  \imath & \jmath & {\it k} \\ 1 & 1 & 1 \\ x & y & z
  \end{array}\right\vert &=\vector{(z-y)}{(x-z)}{(y-z)}\\
  \therefore \vector{}{1}{-1}&=\vector{(z-y)}{(x-z)}{(y-z)} \\
  \implies z-y&=0,\\ x-z&=1,\\ y-x&=-1
\end{align}
