%text If $a,b\in\mathbb{R}$ and $n\in\mathbb{Z^+}$ (positive integers), then % \begin{align} (a&+b)^n = \sum_{k=0}^n\, ^{n}C_k \cdot a^{n-k}\cdot b^k \\ &= \underbrace{^{n}C_0 \cdot a^n b^0 + ^{n}C_1\cdot a^{n-1}b +\ldots+^{n}C_n\cdot a^0b^n}_{n+1\text{ terms}} \end{align}
%text
\underline\text{All other expansions,} like $(a-b)^n, (1+x)^n$ are simply different versions of $(a+b)^n$.
\textbf{General Term \& Coefficient}
% \begin{tabular}{|c|c|c|} \hline & \text{Expression} & \text{Notes} \\ \hline (m+1)\text{-Term} & {^n}C_m\cdot a^{n-m}\cdot b^m & 0\leq m \leq n \\ \hline (m+1)\text{-Coeff} & {^n}C_m & 0\leq m \leq n \\ \hline \end{tabular} %text
\textbf{Note the following} For the first term, $m=0$ (not $m=1$). For the second term, $m=1$ (not $m=2)$. For the last term, $m = n$. $^{n}C_{n+1}$ is \underline\text{not} defined. Hence, $m\leq n$. %
